import React, { Component } from 'react'
import ApiService from '../service'

export default class Pets extends Component {
    state = {
        cats:[],
        catPos: 0,
        isAvailable: true
    }

    componentDidMount(){
        ApiService.handleGetCats()
            .then(cats => 
                this.setState({
                    cats: cats, 
                    isAvailable: false
                })
            )
    }

    nextCat = () => {
        this.setState({
            catPos: this.state.catPos + 1
        })
    }

    previousCat = () => {
        this.setState({
            catPos: this.state.catPos - 1
        })
    }

    handleAdoptCat = () => {
        ApiService.handleCatAdopt()
            .then(res => {
                let { cats } = this.state
                cats.shift()
                this.setState({ cats: cats})
                this.props.restPeoplePos()
            })
            .catch(e => console.log(e))
    }

    render() {
        const { catPos } = this.state
        const cat = this.state.cats[this.state.catPos]
        let isAvailable = true
        if(catPos !== 0) isAvailable = false
        if(this.props.position !== 0) isAvailable = false

        return(
            <div>
                <button onClick={this.previousCat} hidden={!catPos}>Previous Cat</button>
                <button onClick={this.nextCat} hidden={catPos === this.state.cats.length-1}>Next Cat</button>

                <img src={cat.imageURL} alt={`The ${cat.breed} for adoption.`} />
                <ul>
                    <li>Name: {cat.name}</li>
                    <li>Age: {cat.age}</li>
                    <li>Breed: {cat.breed}</li>
                    <li>Description: {cat.description}</li>
                    <li>Gender: {cat.gender}</li>
                    <li>Story: {cat.story}</li>
                </ul>

                {isAvailable === true && <button type="button" onClick={this.handleAdoptCat()} >Adopt Cat</button>}
            </div>
        )
    }

}
