import React, { Component } from 'react'
import Dog from '../Pets/Dog'
import Cat from '../Pets/Cat'
import ApiService from '../service'
import config from '../config'

class AdoptPage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            dogs: [],
            cats: [],
            dogNode: null,
            catList: [],
            people: [],
            count: 0,
        }
    }

    /*handleDogAdopt = () => {
        ApiService.handleDogAdopt()
            .then(dogs => {
                this.setState({ dogs, dogNode: dogs.first })
                console.log(dogs)
            })
            .then(res =>
                ApiService.handleDeletePerson()
                    .then(people => {
                        this.setState({ people })
                        this.props.history.push('/')
                    })
            )
            .catch(e => console.log(e))
    }*/

    componentDidMount() {
        Promise.all([
            fetch(`${config.API_ENDPOINT}/people`),
            fetch(`${config.API_ENDPOINT}/cats`),
            fetch(`${config.API_ENDPOINT}/dogs`),
        ])
            .then(([peopleRes, catsRes, dogsRes]) => {
                if (!peopleRes.ok)
                    return peopleRes.json().then(e => Promise.reject(e))
                if (!catsRes.ok)
                    return catsRes.json().then(e => Promise.reject(e))
                if (!dogsRes.ok)
                    return dogsRes.json().then(e => Promise.reject(e))
                return Promise.all([
                    peopleRes.json(),
                    catsRes.json(),
                    dogsRes.json(),
                ])
            })
            .then(([people, cats, dogs]) => {
                this.setState({ people, cats, dogs })
                console.log(people, cats, dogs)
            })
            .catch(error => {
                console.error({ error });
            });
    }

    /*handleCatAdopt = () => {
        ApiService.handleCatAdopt()
            .then(cats => {
                this.setState({ catList: this.state.catList })
            })
            .then(res =>
                ApiService.handleDeletePerson().then(person => {
                    this.setState({ person })
                    this.props.history.push('/')
                }))
            .catch(e => console.log(e))
    }

    handleAddPerson = () => {
        ApiService.handleAddPerson()
            .then(newName => {
                this.setState({ people: newName })
            })
    }*/

    render() {
        console.log(this.state.dogs)
        console.count('counter')
        return (
            <div>
                <h1>Adoption</h1>
                {/*this.state.count < 1 && (<p>Sorry, no more adoption available at this time</p>)*/}

                <section>
                    <button>See Next</button>

                        <Dog
                            count={this.state.count}
                            handleDogAdopt={this.handleDogAdopt}
                            dogs={this.state.dogs} />

                </section>

                {/*<section>
                    <button>See Next</button>
                    {this.state.cats !== null(
                        <Cat
                            count={this.state.count}
                            handleCatAdopt={this.handleCatAdopt}
                            cats={this.state.cats} />
                    )}
                    </section>*/}

            </div>
        )
    }
}

export default AdoptPage
