import React, { Component } from 'react'

export default class Pets extends Component {
    render(){
        return (
            <div>
                <img src={this.props.petPhoto} alt={`The ${this.props.breed} for adoption.`} />
                <ul>
                    <li>Name: {this.props.name}</li>
                    <li>Age: {this.props.age}</li>
                    <li>Breed: {this.props.breed}</li>
                    <li>Description: {this.props.description}</li>
                    <li>Gender: {this.props.gender}</li>
                    <li>Story: {this.props.story}</li>
                </ul>

                <button type="button"  onClick={() => this.props.handleDogAdopt()}>Adopt Dog</button>

                <button type="button"  onClick={() => this.props.handleCatAdopt()}>Adopt Cat</button>

            </div>
        )
    }

}
