import React from 'react'

export default class People extends React.Component {

  render() {
    const people = []
    const { peoplePos } = this.props
    if (this.props.peopleQ) {
      this.props.peopleQ.forEach((person, i) => {
        people.push(
          <li key={i}>{person.name}</li>
        )
      })
    }
    return (
      <div>
        <h3>
          Waiting List
        </h3>
        <ul>Next up: {people}</ul>
        <p>
          {peoplePos===0 ? "It's your turn to pick a pet!" : (peoplePos!==null ? `Your peoplePos in the Queue is ${peoplePos+1}` : '')}
        </p>
      </div>
    )
  }
}
