import React, { Component } from 'react'
import { Switch, Route } from 'react-router-dom'
import config from '../config'
import './Root.css'
import Header from '../Header/Header'
import Dashboard from '../Dashboard/Dashboard'
import AdoptPage from '../AdoptPage/AdoptPage'


class Root extends Component {
  constructor(props) {
    super(props)
    this.state = {
      dogs: {},
      cats: {},
      people: [],
      person: {}
    }
  }

  componentDidMount() {
    Promise.all([
      fetch(`${config.API_ENDPOINT}/people`),
      fetch(`${config.API_ENDPOINT}/cats`),
      fetch(`${config.API_ENDPOINT}/dogs`)
    ])
      .then(([people, catsRes, dogsRes]) => {
        if (!people.ok)
          return people.json().then(e => Promise.reject(e))
        if (!catsRes.ok)
          return catsRes.json().then(e => Promise.reject(e))
        if (!dogsRes.ok)
          return dogsRes.json().then(e => Promise.reject(e))
        return Promise.all([people.json(), catsRes.json(), dogsRes.json()])
      })
      .then((people, cats, dogs) => {
        this.setState({ people, cats, dogs })
      })
      .catch(error => {
        console.error({ error });
      });
  }


  render() {
    const value = {
      cats: this.state.cats,
      dogs: this.state.dogs,
      people: this.state.people
    }
    return (

      <div>
        <Header />

        <Switch>
          <Route exact path='/' component={Dashboard} />
          <Route value={value} path='/adopt' component={AdoptPage} />
        </Switch>
      </div>

    )
  }
}

export default Root
