import React, { Component } from 'react'
import Dog from '../Pets/Dog'
import Cat from '../Pets/Cat'
import People from '../People/People'
import ApiService from '../service'

class AdoptPage extends Component {
    state = {
        people: [],
        peoplePos: null
    }

    checkPeoplePos = () => {
        const people = this.state.people
        const peoplePos = people.length
        console.log("checkpeoplepos: ", people.length)
        this.setState({
            peoplePos: peoplePos
        })
    }

    componentDidMount() {
        ApiService.handleGetPeople()
            .then(people => 
                this.setState({
                    people: people,
                    peoplePos: this.checkPeoplePos(),
                    loadingPeople: false
                }, console.log("componentDidMount", people, this.state.peoplePos)))
    }

