import React, { Component } from 'react'
import ApiService from '../service'

export default class Pets extends Component {
    state = {
        dogs:[],
        dogPos: 0
    }

    componentDidMount(){
        ApiService.handleGetDogs()
            .then(dogs => this.setState({
                    dogs: dogs
                })
            )
    }

    nextDog = () => {
        this.setState({
            dogPos: this.state.dogPos + 1
        })
    }

    previousDog = () => {
        this.setState({
            dogPos: this.state.dogPos - 1
        })
    }

    /*handleAdoptDog = () => {
        console.log("is this passing?")
        ApiService.handleDogAdopt()
            .then(res => {
                window.alert('Congrats! You adopted a dog!')
                let { dogs } = this.state
                dogs.shift()
                this.setState({ dogs: dogs})
                this.props.resetPeoplePos()
            })
            .catch(e => console.log(e))
    }*/

    render() {
        const dogs = this.state.dogs
        const dogPos = dogs.length

        let isAvailable = true
        if(dogPos !== 0) isAvailable = false
        if(this.props.peoplePos !== 0) isAvailable = false


        const dogTest = dogs.length
        console.log("dogs: ", dogs)
        console.log("dogTest: ", dogTest)

        return(
            <div>
                <button onClick={this.previousDog} hidden={!dogPos}>Previous Dog</button>
                <button onClick={this.nextDog} hidden={dogPos === dogs.length-1}>Next Dog</button>

                {dogs.map(dog => (
                    <ul>    
                    <img src={dog.imageURL} alt="description" />
                    <li>Name: {dog.name}</li>
                    <li>Age: {dog.age}</li>
                    <li>Breed: {dog.breed}</li>
                    <li>Description: {dog.description}</li>
                    <li>Gender: {dog.gender}</li>
                    <li>Story: {dog.story}</li>
                </ul>))}


                {/*isAvailable === true && <button type="button" onClick={this.handleAdoptDog()} >Adopt Dog</button>*/}
            </div>
        )
    }

}
