import React, { useContext, Component } from 'react'
import Context from '../Context'

class AdoptPage extends Component {
    static contextType = Context

    state = {
        dogs: {
            age: 0,
            breed: '',
            gender: '',
            petPhoto: '',
            name: '',
            story: '',
            isAvailable: true
        },
        cats: {
            age: 0,
            breed: '',
            gender: '',
            petPhoto: '',
            name: '',
            story: '',
            isAvailable: true
        },
        people: [],
        person: {
            name: '',
            touched: false,
            isQueued: ''
        }
    }



    handleDogsNotAvailable() {
        if (Object.keys(this.state.dogs).length === 0) {
            this.setState({
                dogs: { isAvailable: false }
            })
        }
    }

    handleCatsNotAvailable() {
        if (Object.keys(this.state.cats).length === 0) {
            this.setState({
                cats: { isAvailable: false }
            })
        }
    }

    render(){
        console.log(this.state)
        return (
            <div>
                <h1>Adoption</h1>


            </div>
        )
    }
}

export default AdoptPage
