import React, { Component } from 'react'
import Context from '../Context'
import config from '../config'

export default class People extends Component {
    static contextType = Context

    constructor(props){
        super(props)
        this.state={
            people: {
                name: '',
                touched: false
            }
        }
    }

    addPeopleName(newName){
        this.setState({
            people:{
                name: newName,
                touched: true
            }
        })
    }

    handleSubmitName = (e) => {
        e.preventDefault()
        const newName = this.state.people.name

        fetch(`${config.API_ENDPOINT}/people`, {
            method: 'POST',
            headers:{
                'content-type': 'application/json'
            },
            body: JSON.stringify({'name': newName})
        }) 
        .then(res => res.json())
        .then(data => {
            this.context.addPerson(data)
            this.props.history.push('/')
        })
        .catch(e => console.log(e))
    }

    render(){
        console.log("people: ", Object.entries(this.context.people)[0])

        const peopleList = Object.entries(this.context.people)[0]
        console.log(typeof peopleList)

        return(
            <div>
                <h3>Current Waiting List</h3>
                <ul>{peopleList}</ul>

                <h4>Interest? Add Name To Join the Waiting List</h4>
                <form onSubmit={e => this.handleSubmitName(e)}>
                    <label>
                        Name: 
                    </label>
                    <input type='text' onChange={e => this.addPeopleName(e.target.name)}></input>
                    <button type='submit'>Submit</button>
                </form>
            </div>
        )
    }
}
