import React, { Component } from 'react'
import Dog from '../Pets/Dog'
import Cat from '../Pets/Cat'
import ApiService from '../service'

class AdoptPage extends Component {
    state = {
        people: [],
        peoplePos: null,
        loadingCats: true,
        loadingDogs: true,
        loadingPeople: true
    }

    checkPeoplePos = () => {
        const people = this.state.people
        const peoplePos = people.length
        this.setState({
            peoplePos: peoplePos
        })
    }

    componentDidMount() {
        ApiService.handleGetPeople()
            .then(people => 
                this.setState({
                    people: people,
                    peoplePos: this.checkPeoplePos(),
                    loadingPeople: false
                }))
    }

    handleAddPerson = (e) => {
        e.preventDefault()
        const newName = {name: e.target.newName.value}
        ApiService.handleAddPerson(newName)
            .then(res => {
                this.setState({
                    people: [...this.state.people],
                    peoplePos: this.checkPeoplePos()
                })
            })
    }

    resetPeoplePosition = () => {
        const people = this.state.people
        people.shift()
        this.setState({ people, peoplePos:null })
        ApiService.handleDeletePerson()
    }

    renderPeople() {
        const people = this.state.people
        return people.forEach(person => {
            person.push(<li>{person.Name}</li>)
        })
    }

    render() {
        const peoplePos = this.state.peoplePos

        console.log(this.state.people)
        return (
            <div>
                <h3>Waiting List</h3>
                <ul>
                    {this.renderPeople()}
                </ul>

                <h4>Interest? Queue up now!</h4>
                {peoplePos !== null ? '' : (
                    <form onSubmit={this.handleAddPerson}>
                        <label>name: </label>
                        <input type='text' placeholder='Random Random' required></input>
                        <button type='submit'>Submit</button>
                    </form>
                )}

                <h3>Pets Currently Available</h3>
                <Dog peoplePos={peoplePos} resetPeoplePosition={this.resetPeoplePosition()}/>

            </div>
        )
    }
}

export default AdoptPage
