import React, { Component } from 'react'
import Context from '../Context'
import config from '../config'

export default class People extends Component {
    static contextType = Context

    addNewName(name){
        const newName = this.state.people
        console.log("newName: ", newName)
        fetch(`${config.API_ENDPOINT}/people`, {
            method: 'POST',
            headers:{
                'content-type': 'application/json'
            },
            body: JSON.stringify({ name })
        }) 
        .then(res => res.json())
        .then(data => {
            this.context.addPerson(data)
            console.log("data: ", data)
        })
        .catch(e => console.log(e))
    }

    render(){
        //console.log("people: ", Object.entries(this.context.people)[0])

        //const peopleList = [Object.values(this.context.people)[0]]
        //console.log(typeof peopleList)

        return(
            <div>
                <h3>Current Waiting List</h3>


                <h4>Interest? Add Name To Join the Waiting List</h4>
                <form onSubmit={e => this.handleSubmitName(e)}>
                    <label>
                        Name: 
                    </label>
                    <input type='text'></input>
                    <button type='submit'>Submit</button>
                </form>
            </div>
        )
    }
}
