import React, { Component } from 'react'
import { Switch, Route } from 'react-router-dom'
import config from '../config'
import './Root.css'
import Header from '../Header/Header'
import Dashboard from '../Dashboard/Dashboard'
import AdoptPage from '../AdoptPage/AdoptPage'


class Root extends Component {
  constructor(props) {
    super(props)
    this.state = {
      dogs: [],
      cats: [],
      people: []
    }
  }

  componentDidMount() {
    Promise.all([
      fetch(`${config.API_ENDPOINT}/people`),
      fetch(`${config.API_ENDPOINT}/cats`),
      fetch(`${config.API_ENDPOINT}/dogs`),
    ])
      .then(([peopleRes, catsRes, dogsRes]) => {
        console.log("dogsRes: ",dogsRes)
        if (!peopleRes.ok)
          return peopleRes.json().then(e => Promise.reject(e))
        if (!catsRes.ok)
          return catsRes.json().then(e => Promise.reject(e))
        if (!dogsRes.ok)
          return dogsRes.json().then(e => Promise.reject(e))
        return Promise.all([
          peopleRes.json(), 
          catsRes.json(), 
          dogsRes.json()
        ])
      })
      .then((people, cats, dogs) => {
        console.log("fetch: ", cats)
        this.setState({ people, cats, dogs })
      })
      .catch(error => {
        console.error({ error });
      });
  }


  render() {
    const value = {
      cats: this.state.cats,
      dogs: this.state.dogs,
      people: this.state.people
    }
    console.log(value)
    return (

      <div>
        <Header />

        <Switch>
          <Route exact path='/' component={Dashboard} />
          <Route value={value} path='/adopt' component={AdoptPage} />
        </Switch>
      </div>

    )
  }
}

export default Root
