import React, { Component } from 'react'
import Pets from '../Pets/Pets'
import Context from '../Context'

class AdoptPage extends Component {
    static contextType = Context
    state = {
        dogs: {
            age: 0,
            breed: '',
            gender: '',
            petPhoto: '',
            name: '',
            story: '',
            isAvailable: true
        },
        cats: {
            age: 0,
            breed: '',
            gender: '',
            petPhoto: '',
            name: '',
            story: '',
            isAvailable: true
        },
        people: [],
        person: {
            name: '',
            touched: false,
            isQueued: ''
        }
    }



