        }
    }

    handleCatsNotAvailable() {
        if (Object.keys(this.state.cats).length === 0) {
            this.setState({
                cats: { isAvailable: false }
            })
        }
    }*/

    render(){
        console.log(this.context)
        return (
            <div>
                <h1>Adoption</h1>
                {this.isAvailable ? <Pets 
                    age={this.state.dogs.age}
                    breed={this.state.dogs.breed}
                    description={this.state.dogs.description}
                    gender={this.state.dogs.gender}
                    petPhoto={this.state.dogs.petPhoto}
                    name={this.state.dogs.name}
                    story={this.state.dogs.story}/> : null}

            </div>
        )
    }
}

export default AdoptPage
